    if (this.pngQuantProcess) {
        this.pngQuantProcess.kill();
        this.pngQuantProcess = null;
    }
    this.bufferedChunks = null;
};

PngQuant.prototype.destroy = function () {
    if (!this.hasEnded) {
        this.hasEnded = true;
        this.cleanUp();
        this.bufferedChunks = null;
    }
